if(context.global.hasOwnProperty("LastMessageTime")){
    var now = ( new Date() ).getTime();
    var lastMessageTime=context.global.LastMessageTime;
    var alertFrequencyChange=context.global.alertFrequencyChange;
    if ((now-lastMessageTime)/1000 >alertFrequencyChange)
    context.global.NeedFlow=1;
    else
     context.global.NeedFlow=0;
 context.global.LastMessageTime=now;
}else
{
    context.global.NeedFlow=1;
    var now = ( new Date() ).getTime();
    context.global.LastMessageTime=now;
}
msg.data=context.global.NeedFlow;

return msg;

if(ActualTemperature > DesiredTemperature ){
    ActualTemperature -= 0.5;
}
else if(ActualTemperature < DesiredTemperature){
    ActualTemperature += 0.5;
}
return msg;

var simulation = flow.get("simulation") || {};
switch (msg.topic) {
case "deviceSchema":
	if(Array.isArray(msg.payload)){
		//all schemas update on startup clean old context 
		simulation = {};
	}
	else{//single schema update while running
		msg.payload = [msg.payload];

	}
	//update schemas
	for (var i = 0; i < msg.payload.length; i++) {
		var schema = msg.payload[i];
		simulation[schema.deviceType] = {schema: schema, devices: {}};
	}
	break;
case "deviceCreated":
	var device = {properties: msg.payload.properties, isOn: true};
	//set properties input types
	device.propertiesInput = {};
	for(var k in device.properties){
		var prpType = typeof(device.properties[k]);
		var inputType = (prpType === "number") ? "number": (prpType === "boolean") ? "checkbox" : "text";
		device.propertiesInput[k] = inputType;
	}
	simulation[msg.payload.deviceType].devices[msg.payload.deviceId] = device;
	break;
case "deviceDeleted":
	var device = msg.payload;
	if(simulation[device.deviceType].devices[device.deviceId]){
		simulation[device.deviceType].devices[device.deviceId].isOn = false;
	}
	else
		return null; //device already deleted
	break;
case "propertyChange":
	var device = msg.payload;
	var changedProperty = device.changedProperty;
	simulation[device.deviceType].devices[device.deviceId].properties[changedProperty.name] = changedProperty.newValue;
	break;
default:
	return null;
}
flow.set("simulation", simulation);
msg.payload = simulation;
return msg;

if(msg.payload === null) return null;

var simulation = flow.get("simulation") || {};
var newDevices = [];
var turnOnMsgs = [];
for(var i = 0; i < msg.payload.length; i++){
    var resp = msg.payload[i];
    if(!resp.deviceId){
        node.error("error on register device");
    }
    else{
         var deviceId = resp.deviceId;
         var deviceType = resp.typeId;
         if(simulation[deviceType]){
            simulation[deviceType].devices[deviceId] = {properties: {}, isOn: false};
            newDevices.push({typeId: deviceType, deviceId: deviceId});
            turnOnMsgs.push({payload: {deviceType: deviceType, deviceId: deviceId}});
         }
    }
}

flow.set("simulation", simulation);
//check if need to push changes to db
msg.payload = simulation; //message to the ui
if(msg.updatedb !== false && newDevices.length > 0){
    var pendingUpdates = flow.get("dbUpdates") || {addDevices: [],deleteDevices: []};
	pendingUpdates.addDevices = pendingUpdates.addDevices.concat(newDevices);
	flow.set("dbUpdates", pendingUpdates);
}
return [msg, turnOnMsgs];

var simulation = flow.get("simulation") || {};
var deviceId = msg.payload.deviceId;
var deviceType = msg.payload.deviceType;
delete simulation[deviceType].devices[deviceId];
flow.set("simulation", simulation);
var uiMsg = {payload: simulation};
var closeDeviceMsg = {payload: {deviceType: deviceType, deviceId: deviceId}};

if(msg.updatedb !== false){
    var pendingUpdates = flow.get("dbUpdates") || {addDevices: [],deleteDevices: []};
	pendingUpdates.deleteDevices.push({typeId: deviceType, deviceId: deviceId});
	flow.set("dbUpdates", pendingUpdates);
}

return [closeDeviceMsg,uiMsg];

var pendingUpdates = flow.get("dbUpdates");
if(!pendingUpdates || (pendingUpdates.addDevices.length === 0 && pendingUpdates.deleteDevices.length === 0)  ) 
return [msg,null];
//load changes on msg
msg.addDevices = pendingUpdates.addDevices;
msg.deleteDevices = pendingUpdates.deleteDevices;
//set doc id
msg.payload =  {_id: "registeredDevices"}

//rest flow pending changes
pendingUpdates.addDevices = [];
pendingUpdates.deleteDevices = [];
flow.set("dbUpdates",pendingUpdates);
return [msg,msg];

var doc = msg.payload || {_id: "registeredDevices", devices: []};
if(msg.addDevices){
	doc.devices = doc.devices.concat(msg.addDevices);
}

function findIndex(array, func){
    for(var i = 0; i < array.length; i++){
        var item = array[i];
        if(func(item) === true){
            return i;
        }
    }
    return -1;
}
if(msg.deleteDevices){  
	for (var i = 0; i < msg.deleteDevices.length; i++) {
		var deviceType = msg.deleteDevices[i].deviceType;
		var deviceId = msg.deleteDevices[i].deviceId;
		var index = findIndex(doc.devices,function(item){return item.deviceId === deviceId;});
		if(index !== -1){
		    doc.devices.splice(index, 1);
		}
	}
}
msg.payload = doc;
return msg;

